import{_ as a,c as s,o as t,ag as n}from"./chunks/framework.DPDPlp3K.js";const h=JSON.parse('{"title":"DB2","description":"","frontmatter":{},"headers":[],"relativePath":"src/Middleware/关系型数据库/DB2.md","filePath":"src/Middleware/关系型数据库/DB2.md","lastUpdated":1737139361000}'),i={name:"src/Middleware/关系型数据库/DB2.md"};function l(d,e,r,p,o,c){return t(),s("div",null,e[0]||(e[0]=[n('<h1 id="db2" tabindex="-1">DB2 <a class="header-anchor" href="#db2" aria-label="Permalink to &quot;DB2&quot;">​</a></h1><h2 id="安装" tabindex="-1">安装 <a class="header-anchor" href="#安装" aria-label="Permalink to &quot;安装&quot;">​</a></h2><p>DB2 Express-C 下载地址：<a href="https://www.ibm.com/support/pages/db2-express-c-download-version-11" target="_blank" rel="noreferrer">https://www.ibm.com/support/pages/db2-express-c-download-version-11</a></p><p>下载后安装即可。</p><h2 id="配置" tabindex="-1">配置 <a class="header-anchor" href="#配置" aria-label="Permalink to &quot;配置&quot;">​</a></h2><p>安装完成后，打开 DB2 命令行工具，输入 <code>db2setdb</code> 命令，配置数据库环境变量。</p><div class="language- vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>db2setdb DB2_DATABASE_NAME</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><h2 id="连接" tabindex="-1">连接 <a class="header-anchor" href="#连接" aria-label="Permalink to &quot;连接&quot;">​</a></h2><p>打开 DB2 命令行工具，输入 <code>db2 connect to &lt;database_name&gt;</code> 命令，连接到数据库。</p><div class="language- vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>db2 connect to &lt;database_name&gt;</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><h2 id="创建数据库" tabindex="-1">创建数据库 <a class="header-anchor" href="#创建数据库" aria-label="Permalink to &quot;创建数据库&quot;">​</a></h2><p>打开 DB2 命令行工具，输入 <code>db2 create database &lt;database_name&gt;</code> 命令，创建数据库。</p><div class="language- vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>db2 create database &lt;database_name&gt;</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><h2 id="删除数据库" tabindex="-1">删除数据库 <a class="header-anchor" href="#删除数据库" aria-label="Permalink to &quot;删除数据库&quot;">​</a></h2><p>打开 DB2 命令行工具，输入 <code>db2 drop database &lt;database_name&gt;</code> 命令，删除数据库。</p><div class="language- vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>db2 drop database &lt;database_name&gt;</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><h2 id="创建表" tabindex="-1">创建表 <a class="header-anchor" href="#创建表" aria-label="Permalink to &quot;创建表&quot;">​</a></h2><p>打开 DB2 命令行工具，输入 <code>db2 create table &lt;table_name&gt; (&lt;column_name&gt; &lt;data_type&gt;, ...)</code> 命令，创建表。</p><div class="language- vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>db2 create table &lt;table_name&gt; (&lt;column_name&gt; &lt;data_type&gt;, ...)</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><h2 id="插入数据" tabindex="-1">插入数据 <a class="header-anchor" href="#插入数据" aria-label="Permalink to &quot;插入数据&quot;">​</a></h2><p>打开 DB2 命令行工具，输入 <code>db2 insert into &lt;table_name&gt; values (&lt;value1&gt;, &lt;value2&gt;, ...)</code> 命令，插入数据。</p><div class="language- vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>db2 insert into &lt;table_name&gt; values (&lt;value1&gt;, &lt;value2&gt;, ...)</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><h2 id="查询数据" tabindex="-1">查询数据 <a class="header-anchor" href="#查询数据" aria-label="Permalink to &quot;查询数据&quot;">​</a></h2><p>打开 DB2 命令行工具，输入 <code>db2 select * from &lt;table_name&gt;</code> 命令，查询数据。</p><div class="language- vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>db2 select * from &lt;table_name&gt;</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><h2 id="更新数据" tabindex="-1">更新数据 <a class="header-anchor" href="#更新数据" aria-label="Permalink to &quot;更新数据&quot;">​</a></h2><p>打开 DB2 命令行工具，输入 <code>db2 update &lt;table_name&gt; set &lt;column_name&gt; = &lt;new_value&gt; where &lt;condition&gt;</code> 命令，更新数据。</p><div class="language- vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>db2 update &lt;table_name&gt; set &lt;column_name&gt; = &lt;new_value&gt; where &lt;condition&gt;</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><h2 id="删除数据" tabindex="-1">删除数据 <a class="header-anchor" href="#删除数据" aria-label="Permalink to &quot;删除数据&quot;">​</a></h2><p>打开 DB2 命令行工具，输入 <code>db2 delete from &lt;table_name&gt; where &lt;condition&gt;</code> 命令，删除数据。</p><div class="language- vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>db2 delete from &lt;table_name&gt; where &lt;condition&gt;</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><h2 id="退出" tabindex="-1">退出 <a class="header-anchor" href="#退出" aria-label="Permalink to &quot;退出&quot;">​</a></h2><p>打开 DB2 命令行工具，输入 <code>exit</code> 命令，退出。</p><div class="language- vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>exit</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><h2 id="参考" tabindex="-1">参考 <a class="header-anchor" href="#参考" aria-label="Permalink to &quot;参考&quot;">​</a></h2><ul><li><a href="https://www.ibm.com/support/pages/db2-express-c-download-version-11" target="_blank" rel="noreferrer">DB2 Express-C 下载地址</a></li><li><a href="https://www.ibm.com/support/producthub/icpdata/docs/content/SSQNUZ_current/cpd/access/db2/db2cli.html" target="_blank" rel="noreferrer">DB2 命令行工具</a></li></ul>',36)]))}const u=a(i,[["render",l]]);export{h as __pageData,u as default};
