import{_ as a,c as l,o as r,ag as t}from"./chunks/framework.DPDPlp3K.js";const u=JSON.parse('{"title":"ElasticSearch","description":"","frontmatter":{},"headers":[],"relativePath":"src/Middleware/非关系型数据库/ElasticSearch.md","filePath":"src/Middleware/非关系型数据库/ElasticSearch.md","lastUpdated":1737139361000}'),i={name:"src/Middleware/非关系型数据库/ElasticSearch.md"};function c(s,e,h,n,o,d){return r(),l("div",null,e[0]||(e[0]=[t('<h1 id="elasticsearch" tabindex="-1">ElasticSearch <a class="header-anchor" href="#elasticsearch" aria-label="Permalink to &quot;ElasticSearch&quot;">​</a></h1><p>ElasticSearch 是一个基于 Lucene 的搜索服务器。它提供了一个分布式多用户能力的全文搜索引擎，基于 RESTful web 接口。ElasticSearch 是用 Java 开发的，并作为 Apache 许可条款下的开放源码发布。</p><p>ElasticSearch 的主要特性包括：</p><ul><li>基于 RESTful web 接口</li><li>分布式多用户能力</li><li>自动搜索分析</li><li>索引自动维护</li><li>多数据源支持</li><li>自动完成</li><li>地理位置搜索</li><li>基于脚本的查询</li><li>实时搜索</li><li>高扩展性</li><li>多语言支持</li><li>安全性</li><li>开源</li></ul><p>ElasticSearch 的安装和使用请参考官方文档：<a href="https://www.elastic.co/guide/en/elasticsearch/reference/current/index.html" target="_blank" rel="noreferrer">https://www.elastic.co/guide/en/elasticsearch/reference/current/index.html</a> ElasticSearch 的中文社区：<a href="https://elasticsearch.cn/" target="_blank" rel="noreferrer">https://elasticsearch.cn/</a> ElasticSearch 的 GitHub 地址：<a href="https://github.com/elastic/elasticsearch" target="_blank" rel="noreferrer">https://github.com/elastic/elasticsearch</a></p><h2 id="使用场景" tabindex="-1">使用场景 <a class="header-anchor" href="#使用场景" aria-label="Permalink to &quot;使用场景&quot;">​</a></h2><p>ElasticSearch 适用于以下场景：</p><ul><li>日志分析：ElasticSearch 可以用于存储和分析大量的日志数据，并提供实时的搜索和分析功能。</li><li>网站搜索：ElasticSearch 可以用于构建网站的全文搜索引擎，提供快速、准确的搜索结果。</li><li>实时数据分析：ElasticSearch 可以用于实时数据分析，提供实时的搜索、分析和图形化展示功能。</li><li>推荐系统：ElasticSearch 可以用于构建推荐系统，通过分析用户行为数据，为用户提供个性化的搜索结果。</li><li>内容推荐：ElasticSearch 可以用于推荐内容，根据用户的搜索习惯、兴趣、偏好等，推荐相关的内容。</li></ul><h2 id="参考资料" tabindex="-1">参考资料 <a class="header-anchor" href="#参考资料" aria-label="Permalink to &quot;参考资料&quot;">​</a></h2><ul><li><a href="https://www.elastic.co/cn/elasticsearch/" target="_blank" rel="noreferrer">ElasticSearch 官网</a></li><li><a href="https://elasticsearch.cn/" target="_blank" rel="noreferrer">ElasticSearch 中文社区</a></li><li><a href="https://github.com/elastic/elasticsearch" target="_blank" rel="noreferrer">ElasticSearch GitHub 地址</a></li></ul>',10)]))}const S=a(i,[["render",c]]);export{u as __pageData,S as default};
